import { getMetadata } from "firebase/storage";
import React, { useCallback, useEffect, useState } from "react";
import Image from "./Image";

const initObj = {
    id : null,
    name : null,
    description : null,
    author : null,
    image
}

const Feed = (props) => {

    const [imagesArr, setImagesArr] = useState([]);
    const [modal, setModal] = useState(false);
    const [modalData, setModalData] = useState([]);

    const getData = (imageObj) => {
        let tempObj = imageObj;
        setModalData(tempObj);
    }

    const fetchImageHandler = useCallback(async () => {
        try{
            const response = await fetch('https://reacthttp-d289b-default-rtdb.firebaseio.com/images.json');
            if(!response.ok){
                throw new Error('Something fucked up !');
            }
            const data = await response.json();

            const loadedImages = [];
            for(const key in data){
                loadedImages.push({
                    id : data[key].key,
                    name : data[key].name,
                    description : data[key].description,
                    author : data[key].author,
                    imageurl : data[key].url,
                    tag : data[key].tag,
                });
            }
            setImagesArr(loadedImages);
        }catch(error){

        }
    },[]);

    useEffect(()=>{
        fetchImageHandler();
    }, [fetchImageHandler])
    return (
        <div className="container">
            <div className="row">
                {imagesArr.map((obj) => {
                    return (
                        <div className="col-md-4" key={obj.id} onClick={()=>{
                            getData(obj);
                        }}>
                            <Image image={obj}/>
                        </div>
                    );
                })}
            </div>
        </div>
    );
}
export default Feed;
