import React, {useRef, useState} from "react";
import { storage } from "../../firebase";
import { getStorage, ref, getDownloadURL, uploadBytes, uploadBytesResumable} from "firebase/storage";
// import {ref, getDownloadURL} from '../../firebase/storage'
const AddImageForm = (props) => {

    // const enteredAuthor = useRef('');
    // const enteredName = useRef('');
    // const enteredDescription = useRef('');
    // const enteredtag = useRef('');
    const [image, setImage] = useState('');
    const [imgurl, setimgurl] = useState('');

    const [enteredAuthor, setEnteredAuthor] = useState('');
    const [enteredName, setEnteredName] = useState('');
    const [enteredTag, setEnteredTag] = useState('');
    const [enteredDescription, setEnteredDescription] = useState('');

    const nameHandler = (event) => {
        setEnteredName(event.target.value);
    }
    const authorHandler = (event) => {
        setEnteredAuthor(event.target.value);
    }
    const tagHandler = (event) => {
        setEnteredTag(event.target.value);
    }
    const descriptionHandler = (event) => {
        setEnteredDescription(event.target.value);
    }

    const metadata = {
        contentType: 'image/jpeg'
    };
    const imageurl = '';
    const upload = () => {
        let ran = Math.trunc(Math.random()*1000);
        const storage = getStorage();
        const refr = ref(storage, `/images/${ran}`);
        const uploadTask = uploadBytesResumable(refr, image, metadata);
        uploadTask.on('state_changed',
        (snapshot) => {
            // const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
            // console.log('Upload is ' + progress + '% done');
            // switch (snapshot.state) {
            // case 'paused':
            //     console.log('Upload is paused');
            //     break;
            // case 'running':
            //     console.log('Upload is running');
            //     break;
            // }
        },
        (error) => {
            // switch (error.code) {
            //     case 'storage/unauthorized':
            //       // User doesn't have permission to access the object
            //       break;
            //     case 'storage/canceled':
            //       // User canceled the upload
            //       break;

            //     // ...

            //     case 'storage/unknown':
            //   // Unknown error occurred, inspect error.serverResponse
            //   break;
            // }
        },
        () => {
            getDownloadURL(uploadTask.snapshot.ref).then((url) => {
                console.log(url);
                console.log("SEXXXXXXXXXXX");
                setimgurl(url);
            })
        }
        )
    }

    // const [Url, setUrl] = useState('');
    // const imageUrl = '';
    // const upload = () => {
    //     console.log("HI !");
    //     if(image === null)return ;
    //     let ran = Math.trunc(Math.random()*1000);
    //     storage.ref(`/images/${ran}`).put(image).on("state_changed", alert("Success"), alert, ()=>{
    //         console.log("HELLOoooo");
    //         // const refr = ref(storage, `/images/${ran}`);
    //         // getDownloadURL(refr).then((url) => {
    //         //     // setUrl(url);
    //         //     imageUrl = url;
    //         //     console.log(url);
    //         // });
    //     });

    // }


    const imageUploadHandler = (event) => {
        setImage(event.target.files[0]);
    }

    const sumbitHandler = async (event) => {
        event.preventDefault();
        upload().then();
        console.log(imgurl);
        console.log(enteredAuthor, enteredDescription, enteredName, enteredTag, imgurl);
        // const image = {
        //     name : enteredName,
        //     author : enteredAuthor,
        //     description : enteredDescription,
        //     tag : enteredtag,
        //     url : imgurl
        // }
    }

    return (
        <div className="container">
            <form onSubmit={sumbitHandler}>                
                <label class="form-label" for="customFile">Default file input example</label>
                <input type="file" class="form-control" id="customFile" onChange={imageUploadHandler}/>
                <div class="form-outline mt-3">
                    <input type="text" id="typeText" class="form-control" onChange={nameHandler}/>
                    <label for="typeText">Image Name</label>
                </div>
                <div class="form-outline mt-3">
                    <input type="text" id="typeText" class="form-control" onChange={descriptionHandler}/>
                    <label for="typeText">Image Description</label>
                </div>
                <div class="form-outline mt-3">
                    <input type="text" id="typeText" class="form-control" onChange={authorHandler}/>
                    <label for="typeText">Author Name</label>
                </div>
                <div class="input-group mb-3 mt-3">
                    <label class="input-group-text" for="inputGroupSelect01">Tag</label>
                    <select class="form-select" id="inputGroupSelect01" onChange={tagHandler} defaultValue={1}>
                        <option selected>Select...</option>
                        <option value="1">Nature</option>
                        <option value="2">Buildings</option>
                        <option value="3">Monuments</option>
                        <option value="4">People</option>
                        <option value="5">Cars</option>
                        <option value="6">Flowers</option>
                    </select>
                </div>
                <div className="d-flex justify-content-center">
                    <div className="row w-50">
                        <button type="submit" className="btn btn-success">Upload</button>
                    </div>
                </div>
            </form>
        </div>
    );
}
export default AddImageForm;
