import React, {useState} from "react";
import { storage } from "../../firebase";
import {ref, uploadBytes} from "firebase/storage";

const AddImageForm = (props) => {
    const [enteredName, setEnteredName] = useState('');
    const [enteredAuthor, setEnteredAuthor] = useState('');
    const [enteredDescription, setEnteredDescription] = useState('');

    const [imageUpload, setImageUpload] = useState(null);

    const nameChangeHandler = (event) => {
        setEnteredName(event.target.value);
    };
    const authorChangeHandler = (event) => {
        setEnteredAuthor(event.target.value);
    };
    const descriptionChangeHandler = (event) => {
        setEnteredDescription(event.target.value);
    };
    const sumbitHandler = (event) => {
        event.preventDefault();
    }

    return (
        <div className="container">
            <form onSubmit={sumbitHandler}>                
                <label class="form-label" for="customFile">Default file input example</label>
                <input type="file" class="form-control" id="customFile" onChange={(event) => {
                    setImageUpload(event.target.files[0]);
                }}/>
                <div class="form-outline mt-3">
                    <input type="text" id="typeText" class="form-control" onChange={nameChangeHandler}/>
                    <label for="typeText">Image Name</label>
                </div>
                <div class="form-outline mt-3">
                    <input type="text" id="typeText" class="form-control" onChange={descriptionChangeHandler}/>
                    <label for="typeText">Image Description</label>
                </div>
                <div class="form-outline mt-3">
                    <input type="text" id="typeText" class="form-control" onChange={authorChangeHandler}/>
                    <label for="typeText">Author Name</label>
                </div>
                <div class="input-group mb-3">
                    <div class="input-group-prepend">
                        <label class="input-group-text" for="inputGroupSelect01">Tag</label>
                    </div>
                    <select class="custom-select" id="inputGroupSelect01">
                        <option selected>Choose...</option>
                        <option value="1">One</option>
                        <option value="2">Two</option>
                        <option value="3">Three</option>
                    </select>
                </div>
                <div className="d-flex justify-content-center">
                    <div className="row w-50">
                        <button type="submit" className="btn btn-success">Upload</button>
                    </div>
                </div>
            </form>
        </div>
    );
}
export default AddImageForm;
