import React, { useCallback, useState } from "react";
import Image from "./Image";

const Feed = (props) => {
    // const arr ={
    //     imageurl:"./img1.jpg",
    //     name: "Image Name1",
    //     description: "Image Description1",
    //     author: "Image Owner1"
    // };
    const [imagesArr, setImagesArr] = useState('');
    const fetchImageHandler = useCallback(async () => {
        try{
            const response = await fetch('https://reacthttp-d289b-default-rtdb.firebaseio.com/images.json');
            if(!response.ok){
                throw new Error('Something fucked up !');
            }
            const data = await response.json();

            const loadedImages = [];
            for(const key in data){
                loadedImages.push({
                    id : key,
                    name : data[key].name,
                    description : data[key].description,
                    author : data[key].author,
                    imageurl : data[key].url,
                    tag : data[key].tag,
                })
            }
            setImagesArr(loadedImages);
        }catch(error){

        }
    });

    return (
        <div className="container">
            <div className="row">
                {loadedImages.map (imagee => (
                    <div className="col-4">
                        <Image onShowDetail={props.onShowDetail} items={imagee}/>
                    </div>
                ))}

            </div>
        </div>
    );
}
export default Feed;
