    const arr ={
        imageurl:"./img1.jpg",
        name: "Image Name1",
        description: "Image Description1",
        author: "Image Owner1"
    };

    const [images, setImages] = useState([]);
    // const [url, setUrl] = useState('');

    const fetchImageHandler = useCallback(async ()=> {

        const response = await fetch ('https://reacthttp-d289b-default-rtdb.firebaseio.com/images.json');
        if(!response.ok){
            throw new Error('Something went wrong');
        }
        const data = await response.json();
        const loadedImages = [];
        for(const k in data){
            const imageurl = '';
            // const imageRef = ref(storage, `image/${data[k].key}`);
            // listAll(imageRef).then((response) => {
            //     response.items.forEach((item) => {
            //         getDownloadURL(item).then((url) => {
            //             imageurl = url;
            //         });
            //     })
            // });
            // storage.ref("image").child(data[k].key).getDownloadURL().then((url) => {
            //     // setUrl(url);
            //     imageurl = url;
            // });
            console.log( data[k].key, data[k].name, data[k].description);
            loadedImages.push({
                imageurl:imageurl,
                id:data[k].key,
                name:data[k].name,
                description:data[k].description,
                author:data[k].author
            });

        }
        console.log("HELLO");
        setImages(loadedImages);
    }, []);

    useEffect(()=> { 
        fetchImageHandler();
    }, [fetchImageHandler]);

    return (
        <div className="container">
            <div className="row">
                {images.map((image) => {
                    <div className="col-4">
                        <Image items={image}/>
                    </div>
                })}
            </div>
        </div>
    );
}
export default Feed;
