import React, {useState} from "react";

const Image = (props) => {
    const imgSrc = './img1.jpg';
    const imgName = props.items.name;
    const imgDes = props.items.description;
    const imgOwner = props.items.author;

    const [showDetail, setShowDetail] = useState(false);

    const detailShowHander = (event) => {
        setShowDetail(true);
    };

    return (
        <div>
            <div class="card my-3 rounded" onClick={detailShowHander}>
                <img src={require(`${imgSrc}`)} class="card-img-top"/>
                <div class="card-body">
                    <h5 class="card-title">{imgName}</h5>
                </div>
            </div>
            {showDetail && 
                <div className="modal-dialog modal-dialog-centered">
                    <div class="card mb-3" style="max-width: 540px;">
                <div class="row g-0">
                    <div class="col-md-4">
                    <img src="..." class="img-fluid rounded-start"/>
                    </div>
                    <div class="col-md-8">
                    <div class="card-body">
                        <h5 class="card-title">Card title</h5>
                        <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                        <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
                    </div>
                    </div>
                </div>
            </div>
                </div>
            }
        </div>
    );
}
export default Image;
