import React, {useCallback, useState} from "react";
import AddImageForm from "./AddImageForm";
import { getDatabase, set } from "firebase/database";
import { ref } from "firebase/storage";

const AddImages = (props) => {
    const[isEditing, setIsEditing] = useState(false);

    function writeData (image) {
        console.log("START");
        const db = getDatabase();
        console.log("BEGIN");
        set(ref(db, 'images/' + image.key), {
            name : image.name,
            description : image.description,
            tag : image.tag,
            author : image.author,
            url : image.url,
            key : image.key
        });
        console.log("IMG UPLOADED 2");
    }

    async function addImageHandler(image){
        const response = await fetch('https://reacthttp-d289b-default-rtdb.firebaseio.com/images/.json', {
            method:'POST',
            body: JSON.stringify(image),
            headers: {
                'Content-type' : 'application/json'
            }
        });
        console.log("IMG UPLOADED 1");
        writeData(image);
        setIsEditing(false);
    }

    const showFormHandler = (event) => {
        setIsEditing(true);
    }

    return (
        <div className="col mt-4">
            <div className="d-flex justify-content-center mb-4">
                <button onClick={showFormHandler} className="btn btn-primary btn-lg">ADD IMAGE</button>
            </div>
            {isEditing && <div >
                <AddImageForm onAddImage={addImageHandler}/>
            </div>}

        </div>
    );
}
export default AddImages;
