import React, {useCallback, useState} from "react";
import AddImageForm from "./AddImageForm";

const AddImages = (props) => {
    const[isEditing, setIsEditing] = useState(false);
    const[isUploading, setIsUploading] = useState(false);
    const[isError, setIsError] = useState(false);
    const fetchMovieHandler = useCallback(async ()=> {

    })


    async function addImageHandler(image){
        setIsUploading(true);
        const response = await fetch('httpsreacthttp-d289b-default-rtdb.firebaseio.com/images.json', {
            method:'POST',
            body: JSON.stringify(image),
            headers: {
                'Content-type' : 'application/json'
            }
        });
        if(response.status !== 200){
            setIsUploading(false);
            setIsError(true);
        };
        setIsEditing(false);
    }

    const showFormHandler = (event) => {
        setIsEditing(true);
    }

    return (
        <div className="col mt-4">
            <div className="d-flex justify-content-center mb-4">
                <button onClick={showFormHandler} className="btn btn-primary btn-lg">ADD IMAGE</button>
            </div>
            {isEditing && <div >
                <AddImageForm onAddImage={addImageHandler}/>
            </div>}

        </div>
    );
}
export default AddImages;
