import { getDownloadURL, listAll, ref } from "firebase/storage";
import React, { useCallback, useState } from "react";
import { storage } from "../../firebase";
import Image from "./Image";

const Feed = (props) => {
    const arr ={
        src:"./img1.jpg",
        name: "Image Name1",
        des: "Image Description1",
        owner: "Image Owner1"
    };

    const [images, setImages] = useState([]);

    const fetchImageHandler = useCallback(async ()=> {
        const response = await fetch ('https://reacthttp-d289b-default-rtdb.firebaseio.com/images.json');
        if(!response.ok){
            throw new Error('Something went wrong');
        }
        const data = await response.json();
        const loadedImages = [];
        for(const k in data){
            const imageurl = '';
            const imageRef = ref(storage, `images/${data[k]}`);
            listAll(imageRef).then((response) => {
                response.items.forEach((item) => {
                    getDownloadURL(item).then((url) => {
                        imageurl = url;
                    });
                })
            });
            loadedImages.push({
                id:data[k].key,
                name:data[k].name,
                description:data[k].description,
                author:data[k].author
            });
        }
        setImages(loadedImages);
    });

    return (
        <div className="container">
            <div className="row">
                <div className="col-4">
                    <Image items={arr}/>
                </div>
            </div>
        </div>
    );
}
export default Feed;
